#!/bin/sh
/var/lib/neo4j/bin/neo4j-admin import --database=flights.db --mode=csv --nodes:Airline="/var/lib/neo4j/import/airlines.csv" --nodes:Airport="/var/lib/neo4j/import/airports.csv" --nodes:Flight="/var/lib/neo4j/import/flights_header.csv,/var/lib/neo4j/import/flights_data_.*" --nodes:AirportDay="/var/lib/neo4j/import/airportDay_header.csv,/var/lib/neo4j/import/airportDay_data_.*" --relationships:FLIES_TO="/var/lib/neo4j/import/fliesTo.csv" --relationships:HAS_DAY="/var/lib/neo4j/import/hasDay_header.csv,/var/lib/neo4j/import/hasDay_data_.*" --relationships="/var/lib/neo4j/import/inFlight_header.csv,/var/lib/neo4j/import/inFlight_data_.*" --relationships="/var/lib/neo4j/import/outFlight_header.csv,/var/lib/neo4j/import/outFlight_data_.*" --ignore-missing-nodes && chmod -R 777 /data && chmod -R 777 /logs